import React, { useState, useEffect }  from 'react';
const Pokedex = require("pokeapi-js-wrapper");


//component that holds title and 3 buttons
function PokemonDetails(props) {
    //chooseSnippet:start new game, records snippet text as state in app.js

    const { chosenPoke } = props;

const detailsContainer = {

image: '',
abilities: [],
stats: [],
type: []

}

//pokedex options
const [pokemonDetails, setPokemonDetails] = useState(detailsContainer);
//error boolean
const [error, setError] = useState(false);

  //fetches api data on first render and stores it in pokedexes
  useEffect(function() {
    const P = new Pokedex.Pokedex();
      async function fetchData() {
          await P.getPokemonByName(chosenPoke).then( response => {

detailsContainer.image = response.sprites
detailsContainer.abilities = response.abilities

          setPokemonDetails(detailsContainer)})
              .catch(err => setError(err))
      }
      fetchData();
  }, []);

    return (
<div>
{error ? "An error has occured with the pokemon API" :
<div>

    <label>Image: </label>
     <img>{pokemonDetails.image.image}</img>
<p>{chosenPoke}</p>
<ul>
    <label>Abilities: </label>
{(pokemonDetails.abilities).map(({ ability }) => <li>{ability.name}</li>)}
</ul>

<ul>
    <label>Base Stats: </label>
    {(pokemonDetails.abilities).map(({ ability }) => <li>{ability.name}</li>)}
</ul>

<ul>
    <label>Type: </label>
    {(pokemonDetails.abilities).map(({ ability }) => <li>{ability.name}</li>)}
</ul>
</div>
}
</div>
         )
}

export default PokemonDetails;
