import React, { useState, useEffect }  from 'react';
const Pokedex = require("pokeapi-js-wrapper");


//component that holds title and 3 buttons
function PokemonDetails(props) {
    //chooseSnippet:start new game, records snippet text as state in app.js

    const { chosenPoke } = props;

    const P = new Pokedex.Pokedex();


//pokedex options
const [pokemonDetails, setPokemonDetails] = useState({});
//error boolean
const [error, setError] = useState(false);

  //fetches api data on first render and stores it in pokedexes
  useEffect(function() {

      async function fetchData() {
          await P.getPokemonByName(chosenPoke).then( response => {
setPokemonDetails({
image: response.sprites,
abilities: response.abilities,
stats: response.stats,
type: response.types

})})
              .catch(err => setError(err))
      }
      fetchData();
  }, []);


    return (
<div>
{error ? "An error has occured with the pokemon API" :
<div>

    <label>Image: "{imageString}"</label>


<p>{chosenPoke}</p>
<ul>
    <label>Abilities: </label>
{(pokemonDetails.abilities).map(({ ability }) => <li key={ability.url}>{ability.name}</li>)}
</ul>

<ul>
    <label>Base Stats: </label>
    {(pokemonDetails.stats).map(({ stat }) => <li key={stat.url}>{stat.name}</li>)}
</ul>

<ul>
    <label>Type: </label>
    {(pokemonDetails.types).map(({ type}) => <li key={type.url}>{type.name}</li>)}
</ul>
</div>
}
</div>
         )
}

export default PokemonDetails;
