import React, { useState, useEffect} from 'react';
import SnippetSelector from './SnippetSelector';
 // import { Pokedex } from 'pokeapi-js-wrapper';
const Pokedex = require("pokeapi-js-wrapper");


function App() {

//what pokedex did user select?
const [chosenDex, setChosenDex] = useState('f');

//pokedex options
const [pokedexes, setPokedexes] = useState([]);

//error boolean
const [hasError, setError] = useState(false);

  //fetches api data on first render and stores it in pokedexes
useEffect(function() {
  const P = new Pokedex.Pokedex();
    async function fetchData() {
        await P.resource("api/v2/pokedex").then( response => setPokedexes(response.results))
            .catch(err => setError(err))
    }
    fetchData();
}, []);





  if (chosenDex == 'f') {


  return (
    <div>
      {hasError ? "An error has occured with the pokemon API" :
      <SelectorButton  setChosenDex={setChosenDex}/>
  }



    </div>
  );
  } else {
    return (
      <div>{chosenDex}</div>
    )
  }

}

export default App;
