    </div>
  );
  } else if (chosenDex != '' && chosenPoke == ''){

    return (
      <div>

<PokemonSelector setChosenPoke={setChosenPoke} chosenDex={chosenDex}/>

      </div>
    )
  } else if ( chosenDex != '' && chosenPoke != '') {

    return (

<PokemonDetails chosenPoke={chosenPoke}/>

    )
  }

}

