import React, { useState, useEffect} from 'react';
const Pokedex = require("pokeapi-js-wrapper");

function SelectorButton(props){
    const { setError, setChosenDex } = props;

//pokedex options
const [pokedexes, setPokedexes] = useState([]);


  //fetches api data on first render and stores it in pokedexes
  useEffect(function() {
    const P = new Pokedex.Pokedex();
      async function fetchData() {
          await P.resource("api/v2/pokedex").then( response => setPokedexes(response.results))
              .catch(err => setError(err))
      }
      fetchData();
  }, []);


    return (
<ul>
        {pokedexes.map(({url, name }) => <li><p>{name}</p><button key={url} onClick={() => setChosenDex(name)}>View</button></li>)}
</ul>
         )



}

export default SelectorButton;
