        await P.resource("api/v2/pokedex").then( response => setPokedexes(response.results))
            .catch(err => setError(err))
    }
    fetchData();
}, []);



  function onSelection(name, url) {
    setChosenDex(name)
  }

  if (chosenDex == 'f') {


  return (
    <div>
      {hasError ? "An error has occured with the pokemon API" :
       pokedexes.map(({url, name }) => <button key={url} onClick={() => onSelection(name, url)}>{name}</button>)}

   <buttonComponent />

    </div>
  );
  } else {
    return (
      <div></div>
    )
  }

}

export default App;
