import React, { useState, useEffect }  from 'react';
const Pokedex = require("pokeapi-js-wrapper");


//component that holds title and 3 buttons
function PokemonSelector(props) {
    //chooseSnippet:start new game, records snippet text as state in app.js

    const { setChosenPoke, pokedex } = props;

//pokedex options
const [pokemon, setPokemon] = useState([]);
//error boolean
const [error, setError] = useState(false);

  //fetches api data on first render and stores it in pokedexes
  useEffect(function() {
    const P = new Pokedex.Pokedex();
      async function fetchData() {
          await P.getPokedexByName(pokedex.name).then( response => setPokemon(response.pokemon_entries))
              .catch(err => setError(err))
      }
      fetchData();
  }, []);


    return (
<div>
        {error ? "An error has occured with the pokemon API" :
<ul>
{pokemon.map(({ entry_number, pokemon_species }) => <li><p>{pokemon_species}</p><button key={entry_number} onClick={() => setChosenPoke(pokemon_species)}>View Details</button></li>)}
</ul> }
</div>
         )


}

export default PokemonSelector;
